        public async Task<bool> SendContractorCount(SendMessageAccountDto ids)
        {   
            var getStatusId = _context.StatusContractor.Where(x => x.Code.Equals(StatusContractorEnum.INVITADO.Description())).Select(s => s.Id).FirstOrDefault();
            var getCredencialUser = _context.UserT.Where(x => x.Id.Equals(ids.UserId)).FirstOrDefault();

            if (ids.ContractorsId.Length > 0)
            {

                foreach (Guid idContractor in ids.ContractorsId)
                {
                    var result = _context.Contractor.Where(x => x.Id.Equals(idContractor)).FirstOrDefault();
                    var resultDetail = _context.DetailProjectContractor.Where(x => x.ContractorId.Equals(idContractor)).FirstOrDefault();
                    if (result != null)
                    {
                        result.ClaveUsuario = await createPassword(result.Correo, getCredencialUser.UserEmail, getCredencialUser.PasswordMail);
                        resultDetail.StatusContractor = getStatusId;
                        _context.Contractor.Update(result);
                        var res = await _context.SaveChangesAsync();
                    }

                }
                return true;
            }
            else
            {
                var listContractor = _context.Contractor.Where(w => w.ClaveUsuario.Equals(NOASIGNADA) && w.DetailProjectContractor.Select(s => s.ContractId).Contains(ids.ContractId)).ToList();

                foreach (var item in listContractor)
                {
                    var resultDetail = _context.DetailProjectContractor.Where(x => x.ContractId.Equals(ids.ContractId) && x.ContractorId.Equals(item.Id)).FirstOrDefault();

                    item.ClaveUsuario = await createPassword(item.Correo, getCredencialUser.UserEmail, getCredencialUser.PasswordMail);
                    resultDetail.StatusContractor = getStatusId;
                    _context.Contractor.Update(item);
                    _context.DetailProjectContractor.Update(resultDetail);

                    var res = await _context.SaveChangesAsync();
                }
                return true;

            }
        }


        private async Task<string> createPassword(string mail,string fromMail, string password)
        {
            var chars = "0123456789ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789-*";

            var stringChars = new char[8];
            var random = new Random();

            for (int i = 0; i < stringChars.Length; i++)
            {
                stringChars[i] = chars[random.Next(chars.Length)];
            }

            var finalString = new String(stringChars);

            if (finalString != null)
            {
                var message = new MailRequestContractor();
                message.FromEmail = fromMail;
                message.Password = password;   
                message.Body = "Cordial saludo señ@r contratista acontinuacion le adjuntamos correo, contraseña y un link de ingreso, para que ingrese y adjunte los diferentes documentos necesarios para tu proceso de contratación" +
                    "CONTRASEÑA ASIGNADA ES:   " + finalString + "    Ingresa con tu correo";
                message.ToEmail = mail;
                message.Subject = "Clave dinamica ";
                await sendMessage(message);
            }

            return finalString;
        }

