        private async Task<bool> SendMessage(MailRequestContractor mailRequest)
        {
            using (var smtp = new MailKit.Net.Smtp.SmtpClient())
            {
                var email = new MimeMessage();
                email.From.Add(MailboxAddress.Parse(_mailSettings.Mail));
                email.To.Add(MailboxAddress.Parse(mailRequest.ToEmail));
                email.Subject = mailRequest.Subject;

                var builder = new BodyBuilder();
                builder.HtmlBody = mailRequest.Body;

                // Agregar imagen Base64 al mensaje
                var imageBytes = Convert.FromBase64String("");
                using (var inputStream = new MemoryStream(imageBytes))
                using (var outputStream = new MemoryStream())
                {
                    using (var imageS = SixLabors.ImageSharp.Image.Load(inputStream))
                    {
                        // Establecer el tamaño máximo deseado de la imagen
                        var maxSize = new SixLabors.ImageSharp.Size(800, 600);

                        // Redimensionar la imagen mientras se mantiene la relación de aspecto
                        imageS.Mutate(x => x.Resize(new ResizeOptions
                        {
                            Size = maxSize,
                            Mode = ResizeMode.Max
                        }));

                        // Guardar la imagen redimensionada en el flujo de salida
                        imageS.Save(outputStream, new JpegEncoder());
                    }

                    // Obtener los bytes de la imagen redimensionada
                    var resizedImageBytes = outputStream.ToArray();
                    var image = new MimePart("image", "jpeg")
                    {
                        Content = new MimeContent(new MemoryStream(imageBytes)),
                        ContentId = MimeUtils.GenerateMessageId()
                    };
                    builder.LinkedResources.Add(image);
                    builder.HtmlBody = $"<p><img src=\"cid:{image.ContentId}\"></p>" + builder.HtmlBody;
                }
                email.Body = builder.ToMessageBody();

                smtp.Connect(_mailSettings.Host, _mailSettings.Port, SecureSocketOptions.StartTls);
                smtp.Authenticate(_mailSettings.Mail, _mailSettings.Password);

                try
                {
                    await smtp.SendAsync(email);
                    return true;
                }
                catch (Exception ex)
                {
                    throw; // Lanzar excepción original
                }
            }
        }

